import React, {useEffect,useState} from 'react';

import Plotly from 'plotly.js-basic-dist';
import createPlotlyComponent from 'react-plotly.js/factory';
const Plot = createPlotlyComponent(Plotly);


export default function Chart() {

    const [temperatures, setTemperatures] = useState([]);
    const [power, setPower] = useState([]);

    useEffect(()=>{
      fetchData();
    },[])

  
    const fetchData = async () =>{
      //here Im fetching data from Express server specified endpoint 
      const data = await fetch("/data"); 
      const _data = await data.json();
      setTemperatures(_data.temperature.values);
      setPower(_data.power.values)
    }

    const makeAxis = (rawData) =>{
        const xAxis = rawData.map((each)=>(
            each.time
        ))
        const yAxis = rawData.map((each)=>(
            each.value
        ))
        const plotData = [xAxis,yAxis]

        return plotData;
    } 


    return (
        <div>
            <Plot
                data={[
                    {
                        type:'scatter',
                        mode:'lines',
                        x:makeAxis(temperatures)[0],
                        y:makeAxis(temperatures)[1]
                    }
                ]}
            />
            <Plot
                data={[
                    {
                        type:'scatter',
                        mode:'lines',
                        x:makeAxis(power)[0],
                        y:makeAxis(power)[1]
                    }
                ]}
            />
        </div>
    )
}
