import './App.css';
import React, {useEffect,useState} from'react';

function App() {

  const [items, setItems] = useState([]);

  useEffect(()=>{
    fetchData();
  },[])


  const fetchData = async () =>{

    const data = await fetch("/data"); //here Im fetching data from Express server specified endpoint 
    const _data = await data.json();
    setItems(_data.temperature.values);
  }

  return (
    <div className="App">
      {items.map((each)=>(
        <p>{each.value}</p>
      ))}
    </div>
  );
}

export default App;
