import React, {useEffect,useState} from 'react';
import Plot from 'react-plotly.js'


export default function Chart() {


    const [tempData, setTempData] = useState([]);

    useEffect(()=>{
      fetchData();
    },[])
  
  
    const fetchData = async () =>{
      
      const data = await fetch("/data"); //here Im fetching data from Express server specified endpoint 
      const _data = await data.json();
      setTempData(_data.temperature.values);
    }


    return (
        <div>
            {/* <Plot
                data={[
                    {
                        type:'scatter',
                        mode:'lines',
                        x:
                    }
                ]}
            /> */}
            {
                tempData.map((each)=>(
                    <div>
                        <p>{each.time}</p>
                        <p>{each.value}</p>
                    </div>
                ))
            }

        </div>
    )
}
